import React, { useState } from 'react';
import './index.scss';
import banner1 from '../../../assets/Images/desk.png';
import banner2 from '../../../assets/Images/desk.png';

const Logo = () => {
    const [currentImage, setCurrentImage] = useState(banner1);
    const [isRotating, setIsRotating] = useState(false);
    const [isWiggling, setIsWiggling] = useState(false);

    const handleImageClick = () => {
        // Start the wiggle animation first
        setIsWiggling(true);
        setTimeout(() => {
            setIsWiggling(false); // Stop wiggling before rotation
            setIsRotating(true); // Start rotation
            setTimeout(() => {
                setCurrentImage(currentImage === banner1 ? banner2 : banner1); // Switch images
                setIsRotating(false); // End rotation after animation
            }, 500); // Match the CSS animation duration for rotation
        }, 500); // Wiggle duration
    };

    return (
        <div className="logo-container" onClick={handleImageClick}>
            <img
                className={`solid-logo 
                    ${isWiggling ? 'wiggle' : ''} 
                    ${isRotating ? 'rotate' : ''}`}
                src={currentImage}
                alt="Logo"
            />
        </div>
    );
};

export default Logo;
